sp_widget.client_script:
api.controller = function ($scope) {
	/* widget controller */
	var c = this;

	/**
	 * Getters and setters
	 */
	var display = [];
	var data = c.data.arrCatItem;
	var useItemTemplate = document.querySelector("[data-item-template]");
	var useItemDiv = document.querySelector("[data-item]");
	var searchInput = document.querySelector("[data-search]");

	/**
	 * Event listener for input
	 * Filters out items that not match input
	 */

	searchInput.addEventListener("input", function (e) {
		var value = e.target.value.toLowerCase();
		display.forEach(function (item) {
			var isVisible = item.name.toLowerCase().includes(value) || item.shortDesc.toLowerCase().includes(value);
			item.element.classList.toggle("hide", !isVisible);
		});
	});
	/**
	 * Handle promise, push object to array.
	 */
	data.forEach(function (item) {
		var card = useItemTemplate.content.cloneNode(true).children[0];
		var sys_id = card.querySelector("[data-sys_id]");
		var name = card.querySelector("[data-name]");
    var shortDesc = card.querySelector("[data-short_description]");
		sys_id.textContent = item.sys_id;
		name.textContent = item.name;
    shortDesc.textContent = item.shortDesc;
		useItemDiv.append(card);
		display.push({ sys_id: item.sys_id, name: item.name, shortDesc: item.shortDesc, element: card });
	});
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  data.arrCatItem = []
  var gr = new GlideRecord('sc_cat_item')
  gr.addQuery('active', 'true')
  gr.query();
  while(gr.next()){
      data.arr.push({
        "type": "sc_cat_item",
        "sys_id": gr.getValue('sys_id'),
        "name": gr.getValue('name'),
        "shortDesc": gr.getValue('short_description')
    })
  }

  var kb = new GlideRecord('kb_knowledge')
  kb.addQuery('workflow_state', 'published');
  kb.query();
  while(kb.next()){
    data.arr.push({
      "type": "kb_knowledge",
      "shortDesc": kb.getValue('short_description'),
      "sys_id": kb.getValue('sys_id'),
      "name": kb.getValue('number')
    })
  }
})();
